const arr = []; 

for (let feed of msg.payload.feeds)
{
    let dateStr = feed.created_at;
    let val = feed.field1;
    const objLog = {}; 

    // // detect abnormality: maybe data is not written yet
    // if (val === "") {
    //     msg.payload = -1;
    //     return msg;
    // }

    let msgLog = "";
    // set topic and log message
    if (val === "3") {
        objLog.topic = "Danger alert";
        msgLog = "Danger alert was sent";
    }

    else if (val === "1" || val === "2") {
        objLog.topic = "Lock status";
        msgLog = (val === "2" ? ": Door was unlocked" : ": Door was locked");
    }

    // Split date and time
    let parts = dateStr.split("T");
    let date = parts[0];
    let timeAndOffset = parts[1];

    // Remove timezone offset
    var timeWithoutOffset = timeAndOffset.split("+")[0];


    objLog.payload = date + ", " + timeWithoutOffset + " " + msgLog; 
    arr.push(objLog); 
}

msg.payload = arr; 
return msg;

